const express = require("express");
const app = express();
const mysql = require("mysql");
const cors = require("cors");

app.use(cors());
app.use(express.json());

const db = mysql.createConnection({
  user: "root",
  host: "localhost",
  password: "mysql123",
  database: "csci3100",
});

app.post("/create", (req, res) => {
  const userID = req.body.userID;
  const name = req.body.name;
  const studyYear = req.body.studyYear;
  const major = req.body.major;
  const age = req.body.age;

  db.query(
    "INSERT INTO user (userID, name, studyYear, major, age) VALUES (?,?,?,?,?)",
    [userID, name, studyYear, major, age],
    (err, result) => {
      if (err) {
        console.log(err);
      } else {
        res.send("Values Inserted");
      }
    }
  );
});

app.get("/user", (req, res) => {
  db.query("SELECT * FROM user ORDER BY userID ASC", (err, result) => {
    if (err) {
      console.log(err);
    } else {
      res.send(result);
    }
  });
});

app.get("/userbyid", (req, res) => {
  const keyword = req.query.keyword;
  db.query("SELECT * FROM user WHERE userID LIKE ? ORDER BY userID ASC",
  [`%${keyword}%`],
    (err, result) => {
    if (err) {
      console.log(err);
    } else {
      res.send(result);
    }
  });
});

app.put("/updatename", (req, res) => {
  const userID = req.body.userID;
  const name = req.body.name;
  db.query(
    "UPDATE user SET name = ? WHERE userID = ?",
    [name, userID],
    (err, result) => {
      if (err) {
        console.log(err);
      } else {
        res.send(result);
      }
    }
  );
});
app.put("/updateyear", (req, res) => {
    const userID = req.body.userID;
    const studyYear = req.body.studyYear;
    db.query(
      "UPDATE user SET studyYear = ? WHERE userID = ?",
      [studyYear, userID],
      (err, result) => {
        if (err) {
          console.log(err);
        } else {
          res.send(result);
        }
      }
    );
  });
  app.put("/updatemajor", (req, res) => {
    const userID = req.body.userID;
    const major = req.body.major;
    db.query(
      "UPDATE user SET major = ? WHERE userID = ?",
      [major, userID],
      (err, result) => {
        if (err) {
          console.log(err);
        } else {
          res.send(result);
        }
      }
    );
  });
  app.put("/updateage", (req, res) => {
    const userID = req.body.userID;
    const age = req.body.age;
    db.query(
      "UPDATE user SET age = ? WHERE userID = ?",
      [age, userID],
      (err, result) => {
        if (err) {
          console.log(err);
        } else {
          res.send(result);
        }
      }
    );
  });
  app.put("/update", (req, res) => {
    const userID = req.body.userID;
    const name = req.body.name;
    const studyYear = req.body.studyYear;
    const major = req.body.major;
    const age = req.body.age;
    db.query(
      "UPDATE user SET name = ? , studyYear = ? , major = ? , age = ? WHERE userID = ?",
      [name, studyYear, major, age, userID],
      (err, result) => {
        if (err) {
          console.log(err);
        } else {
          res.send(result);
        }
      }
    );
  });

app.delete("/delete/:id", (req, res) => {
  const id = req.params.id;
  db.query("DELETE FROM user WHERE userID = ?", id, (err, result) => {
    if (err) {
      console.log(err);
    } else {
      res.send(result);
    }
  });
});

app.listen(3001, () => {
  console.log("Yey, your server is running on port 3001");
});